-- First ensure key column is unique and then insert translations
CREATE UNIQUE INDEX IF NOT EXISTS ui_strings_key_unique ON ui_strings (key);

-- Insert all static translations from translations.ts into ui_strings table
ON CONFLICT (key) DO UPDATE SET
  text_uk = EXCLUDED.text_uk,
  text_ru = EXCLUDED.text_ru,
  text_pl = EXCLUDED.text_pl,
  text_en = EXCLUDED.text_en,
  category = EXCLUDED.category,
  updated_at = now();